Function Show-SonicHack
{
    <#
    .SYNOPSIS

    Shows the form "sonic is hacking your computer [wtf]"
    #>

    [void][reflection.assembly]::LoadWithPartialName("System.Windows.Forms")

    $imgBytes = [System.Convert]::FromBase64String("")
    $memStream = New-Object System.IO.MemoryStream(,$imgBytes)

    $img = [System.Drawing.Image]::FromStream($memStream)

    $form = New-Object Windows.Forms.Form
    $form.MaximizeBox = $false
    $form.MinimizeBox = $false
    $form.ShowIcon = $false
    $form.FormBorderStyle = [System.Windows.Forms.FormBorderStyle]::None
    $form.ShowInTaskbar = $false
    $form.TopMost = $true
    $form.StartPosition = [System.Windows.Forms.FormStartPosition]::CenterScreen

    $form.Width = 314
    $form.Height = 162

    $pictureBox = New-Object Windows.Forms.PictureBox
    $pictureBox.Width =  $img.Size.Width
    $pictureBox.Height = $img.Size.Height
    $pictureBox.Image = $img
    

    $button = New-Object System.Windows.Forms.Button
    $button.Text = 'WTF'
    $button.Left = $img.Size.Width / 2.5
    $button.Top = $img.Size.Height / 1.5
    $button.Width = $img.Size.Width / 3.4
    $button.Height = $img.Size.Height / 5
    $button.Font = New-Object System.Drawing.Font('Ariel', 12.5)
    $button.Cursor = [System.Windows.Forms.Cursors]::Hand
    $button.DialogResult = [System.Windows.Forms.DialogResult]::Cancel

    $form.Controls.Add($button)
    $form.controls.add($pictureBox)

    $form.Add_Shown(
    {
        $form.Activate()
    });
    
    try
    {
        $sound = New-Object System.Media.SoundPlayer;
        $sound.SoundLocation = "$($Env:WinDir)\Media\Windows Exclamation.wav";
        $sound.Play();
    }
    catch
    {
        Write-Error $_
    }

    $form.ShowDialog() | Out-Null
}